import React, { Component } from "react";
import "./App.css";

class App extends Component {
  state = {
    deadline: "",
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0
  };

  inputHandler = event => {
    const dateFormat = { year: "numeric", month: "long", day: "2-digit" };
    this.setState({
      deadline: new Date(event.target.value).toLocaleString("en-US", dateFormat)
    });
  };

  countDown = () => {
    this.countDown = setInterval(date => {
      var now = Date.parse(new Date());
      var distance = Date.parse(date) - now;
      this.setState({
        days: Math.floor(distance / (1000 * 60 * 60 * 24)),
        hours: Math.floor(
          (distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
        ),
        minutes: Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60)),
        seconds: Math.floor((distance % (1000 * 60)) / 1000)
      });
    }, 1000);
  };

  render() {
    const statement =
      this.state.days +
      "days" +
      this.state.hours +
      "hours" +
      this.state.minutes +
      "minutes" +
      this.state.seconds +
      "seconds";
    return (
      <div className="App">
        <div>
          <h2>Count down to {this.state.deadline}</h2>
          {statement}
          <input
            placeholder="enter new date"
            type="date"
            onChange={this.inputHandler}
          />
          <button onClick={this.countDown}>Submit</button>
        </div>
      </div>
    );
  }
}

export default App;
